import  React ,{useReducer} from 'react';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';
import Input from '@material-ui/core/Input';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

export default function BasicModal(props) {
    const query={
        q:'',
        doms:''
    }

    function reducer(state, action) {
        switch (action.type) {
          case 'q':
            return { q: action.payload };
          case 'doms':
            return { doms: action.payload };
          default:
            throw new Error();
        }
      }
      const [state, dispatch] = useReducer(reducer, query);
//   const [q, setq] = React.useState({q:'',
//                                     doms:''});
//   const [doms, setDoms] = React.useState('');

  console.log(props)
//   const handleOpen = () => setOpen(true);
  const handleClose = () => props.setOpen(false);

  return (
    <div>
      {/* <Button onClick={handleOpen}>Open modal</Button> */}
      <Modal
        open={props.open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
                  <Input placeholder="Search by Title/Desc" value={state.q} onChange={(event) => {
                      dispatch({
                          type: 'q',
                          payload: event.target.value
                      })
                  }} />
                  <Input placeholder="Enter comma seperated domain names" value={state.doms} onChange={(event) => {
                      dispatch({
                          type: 'doms',
                          payload: event.target.value
                      })
                  }} />
          {/* <Typography id="modal-modal-title" variant="h6" component="h2">
            Text in a modal
          </Typography>
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
          </Typography> */}
        </Box>
      </Modal>
    </div>
  );
}
